
def search(pat, txt):
    # code here
    mapping = dict()
    k = len(pat)
    n = len(txt)
    for i in range(k):
        if pat[i] not in mapping.keys():
            mapping[pat[i]] = 1
        else:
            mapping[pat[i]] += 1
    count = len(mapping)
    ans = 0
    i = 0
    for j in range(n):
        if txt[j] in mapping:
            mapping[txt[j]] -= 1
            if mapping[txt[j]] == 0:
                count -= 1

        if j >= k - 1:
            if count == 0:
                ans += 1

            if txt[i] in mapping:
                mapping[txt[i]] += 1
                if mapping[txt[i]] == 1:
                    count += 1
            i += 1
    return ans
txt = ""
pat = "cujauhfsopxmcmjzumoaqzqvhrmfrbuppqusgvdqzbxc"
print(search( pat, txt))
